sys_ux_data_broker_transform.script:
function transform(input) {


    var tableName = input.table,
        sysId = input.recordId;
    var user = gs.getUserID();
	gs.info('@MRT:- IN DB'+tableName+'-->'+sysId);
    var glideAlert = new GlideRecord(tableName);
    if (glideAlert.get(sysId)) {
        var readUsers = (glideAlert.list_of_read_users).toString();
        if (readUsers.indexOf(user) == -1) {
            glideAlert.list_of_read_users = [glideAlert.list_of_read_users, ',', user].join('');
            glideAlert.update();
            return true;
        } else {
            return true;
        }
    }
    return false;
}
