sys_ux_data_broker_transform.script:
function transform(input) {
    var selfReport = new GlideRecord(input["table"]);
    if (!selfReport.get(input["recordId"]))
        return {};
    var selfReoprtObject = {
        "breadcrumb":"Back to "+selfReport.number + " " + selfReport.getDisplayValue("type_of_emergency")
    };
    return selfReoprtObject;
}
