sys_ux_data_broker_transform.script:
function transform() {

    var data = [];
    var icon = {
        "emergency_information": "circle-exclamation-outline",
        "community_update": "megaphone-outline"
    };
	var month = {
		"1":"January",
		"2":"February",
		"3":"March",
		"4":"April",
		"5":"May",
		"6":"June",
		"7":"July",
		"8":"August",
		"9":"September",
		"10":"October",
		"11":"November",
		"12":"December"
	};
    var communityAlert = new GlideRecord('x_snc_emergency_al_emergency_alert');
    communityAlert.addActiveQuery();
    communityAlert.query();
    while (communityAlert.next()) {

        var date = new GlideDateTime(communityAlert.sys_created_on);
        var alert = {};
        var title = {};
        var fields = [];
        title.label = communityAlert['emergency_alert_title'].toString();
        title.opensWindow = 'false';
		title.href=[gs.getProperty('glide.servlet.uri'),'now/emergency/emergency-information/x_snc_emergency_al_emergency_alert_list/',communityAlert.sys_id,'/',communityAlert.kb_article].join('');
        fields.push({
            "text": communityAlert.getDisplayValue('type'),
            "type": "string"
        });
        fields.push({
            "text": [month[date.getMonthLocalTime()],date.getDayOfMonthLocalTime()].join(' '),
            "type": "string"
        });
        alert.icon = icon[communityAlert['type'].toString()];
        alert.title = title;
        alert.fields = fields;
        data.push(alert);
    }

    return data;
}
